import React, { useState, useEffect } from 'react';
import {
  Box,
  Container,
  Typography,
  Paper,
  Grid,
  CircularProgress,
  TextField,
  Button,
  Slider,
  Divider,
  Tabs,
  Tab,
} from '@mui/material';
import { ThemeProvider, createTheme } from '@mui/material/styles';

interface Stop {
  stop_id: number;
  stop_name: string;
  latitude: number;
  longitude: number;
  routes: string[];
  distance: number;
}

interface Arrival {
  route: string;
  destination: string;
  arrival_time: string;
  minutes: number;
  is_delayed: boolean;
  route_color?: string;
}

interface StopsResponse {
  train_stops: Stop[];
  bus_stops: Stop[];
}

const theme = createTheme({
  palette: {
    primary: {
      main: '#1976d2',
    },
    secondary: {
      main: '#dc004e',
    },
  },
});

function App() {
  const [location, setLocation] = useState<[number, number]>(DEFAULT_LOCATION);
  const [loading, setLoading] = useState(false);
  const [stops, setStops] = useState<StopsResponse>({ train_stops: [], bus_stops: [] });
  const [monitoredStops, setMonitoredStops] = useState<Stop[]>([]);
  const [error, setError] = useState<string | null>(null);
  const [tabValue, setTabValue] = useState(0);  // Default to arrivals view
  const [searchRadius, setSearchRadius] = useState(0.5);

  // Load monitored stops from backend on mount
  useEffect(() => {
    const loadMonitoredStops = async () => {
      try {
        const response = await fetch('http://localhost:8000/api/monitored-stops');
        if (response.ok) {
          const data = await response.json();
          setMonitoredStops(data);
          // Set tab value based on whether we have monitored stops
          setTabValue(data.length > 0 ? 0 : 1);
        }
      } catch (err) {
        console.error('Error loading monitored stops:', err);
      }
    };
    loadMonitoredStops();
  }, []);

  const handleLocationUpdate = (newLocation: [number, number]) => {
    setLocation(newLocation);
  };

  const getLocation = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition((position) => {
        setLocation([position.coords.latitude, position.coords.longitude]);
      });
    }
  };

  const handleStopSelect = (stop: Stop) => {
    // Implementation of handleStopSelect
  };

  const handleStopRemove = (stopId: number) => {
    // Implementation of handleStopRemove
  };

  return (
    <ThemeProvider theme={theme}>
      <Container maxWidth="lg" sx={{ py: 4 }}>
        <Typography variant="h4" component="h1" gutterBottom>
          CTA Transit Tracker
        </Typography>

        {error && (
          <Paper sx={{ p: 2, mb: 2, bgcolor: '#ffebee' }}>
            <Typography color="error">{error}</Typography>
          </Paper>
        )}

        <Box sx={{ borderBottom: 1, borderColor: 'divider' }}>
          <Tabs value={tabValue} onChange={(_, newValue) => setTabValue(newValue)}>
            <Tab label="Arrival Times" />
            <Tab label="Find Stops" />
          </Tabs>
        </Box>

        <TabPanel value={tabValue} index={0}>
          {monitoredStops.length > 0 ? (
            <Grid container spacing={2}>
              {monitoredStops.map((stop) => (
                <Grid item xs={12} key={stop.stop_id}>
                  <Box sx={{ position: 'relative' }}>
                    <Button
                      size="small"
                      color="error"
                      onClick={() => handleStopRemove(stop.stop_id)}
                      sx={{ position: 'absolute', right: 8, top: 8, zIndex: 1 }}
                    >
                      Remove
                    </Button>
                    <ArrivalsList stop={stop} />
                  </Box>
                </Grid>
              ))}
            </Grid>
          ) : (
            <Box sx={{ textAlign: 'center', mt: 4 }}>
              <Typography variant="h6" gutterBottom>
                No stops monitored
              </Typography>
              <Button
                variant="contained"
                onClick={() => setTabValue(1)}
                sx={{ mt: 2 }}
              >
                Find Stops
              </Button>
            </Box>
          )}
        </TabPanel>

        <TabPanel value={tabValue} index={1}>
          <LocationSettings
            onLocationUpdate={handleLocationUpdate}
            loading={loading}
            onUseCurrentLocation={getLocation}
          />
          
          <Grid container spacing={2}>
            <Grid item xs={12} md={8}>
              <Paper sx={{ p: 2, mb: 2 }}>
                <StopsMap
                  trainStops={stops.train_stops}
                  busStops={stops.bus_stops}
                  center={location}
                  onStopSelect={handleStopSelect}
                />
              </Paper>
            </Grid>
            
            <Grid item xs={12} md={4}>
              <StopsList
                trainStops={stops.train_stops}
                busStops={stops.bus_stops}
                onStopSelect={handleStopSelect}
                monitoredStops={monitoredStops}
              />
            </Grid>
          </Grid>
        </TabPanel>
      </Container>
    </ThemeProvider>
  );
}

export default App;
