import React, { useEffect, useRef } from 'react';
import { MapContainer, TileLayer, Marker, Popup, useMapEvents } from 'react-leaflet';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';

// Fix for default marker icons in React Leaflet
import icon from 'leaflet/dist/images/marker-icon.png';
import iconShadow from 'leaflet/dist/images/marker-shadow.png';
import iconRetina from 'leaflet/dist/images/marker-icon-2x.png';

interface Stop {
  stop_id: number;
  stop_name: string;
  latitude: number;
  longitude: number;
  routes: string[];
  distance: number;
}

interface StopsMapProps {
  trainStops?: Stop[];
  busStops?: Stop[];
  center: [number, number];
  onStopSelect: (stop: Stop) => void;
}

// Create custom icons for train and bus stops
const trainIcon = new L.Icon({
  iconUrl: 'https://raw.githubusercontent.com/pointhi/leaflet-color-markers/master/img/marker-icon-2x-red.png',
  shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png',
  iconSize: [25, 41],
  iconAnchor: [12, 41],
  popupAnchor: [1, -34],
  shadowSize: [41, 41]
});

const busIcon = new L.Icon({
  iconUrl: 'https://raw.githubusercontent.com/pointhi/leaflet-color-markers/master/img/marker-icon-2x-blue.png',
  shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png',
  iconSize: [25, 41],
  iconAnchor: [12, 41],
  popupAnchor: [1, -34],
  shadowSize: [41, 41]
});

// Set default icon options
const defaultIcon = new L.Icon({
  iconUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png',
  iconRetinaUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon-2x.png',
  shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png',
  iconSize: [25, 41],
  iconAnchor: [12, 41],
  popupAnchor: [1, -34],
  shadowSize: [41, 41]
});

L.Marker.prototype.options.icon = defaultIcon;

interface MapContentProps extends StopsMapProps {
  center: [number, number];
}

// Component to render map content (markers, etc.)
function MapContent({ trainStops = [], busStops = [], center, onStopSelect }: MapContentProps) {
  const map = useMapEvents({
    // Update map view when center changes
    load: () => {
      map.setView(center, map.getZoom());
    }
  });

  useEffect(() => {
    map.setView(center, map.getZoom());
  }, [center, map]);

  return (
    <>
      <TileLayer
        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
      />
      
      {/* Render train stops */}
      {trainStops.map((stop) => (
        <Marker
          key={`train-${stop.stop_id}`}
          position={[stop.latitude, stop.longitude]}
          icon={trainIcon}
          eventHandlers={{
            click: () => onStopSelect(stop)
          }}
        >
          <Popup>
            <div>
              <h3>{stop.stop_name}</h3>
              <p>Routes: {stop.routes.join(', ')}</p>
              <p>Distance: {stop.distance.toFixed(2)} miles</p>
              <button 
                onClick={() => onStopSelect(stop)}
                style={{
                  padding: '8px 16px',
                  backgroundColor: '#1976d2',
                  color: 'white',
                  border: 'none',
                  borderRadius: '4px',
                  cursor: 'pointer'
                }}
              >
                Add to Monitoring
              </button>
            </div>
          </Popup>
        </Marker>
      ))}

      {/* Render bus stops */}
      {busStops.map((stop) => (
        <Marker
          key={`bus-${stop.stop_id}`}
          position={[stop.latitude, stop.longitude]}
          icon={busIcon}
          eventHandlers={{
            click: () => onStopSelect(stop)
          }}
        >
          <Popup>
            <div>
              <h3>{stop.stop_name}</h3>
              <p>Routes: {stop.routes.join(', ')}</p>
              <p>Distance: {stop.distance.toFixed(2)} miles</p>
              <button 
                onClick={() => onStopSelect(stop)}
                style={{
                  padding: '8px 16px',
                  backgroundColor: '#1976d2',
                  color: 'white',
                  border: 'none',
                  borderRadius: '4px',
                  cursor: 'pointer'
                }}
              >
                Add to Monitoring
              </button>
            </div>
          </Popup>
        </Marker>
      ))}
    </>
  );
}

// Main map component
const StopsMap: React.FC<StopsMapProps> = (props) => {
  const mapRef = useRef<L.Map | null>(null);
  const containerRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    // Check if map is already initialized
    if (!mapRef.current && containerRef.current) {
      mapRef.current = L.map(containerRef.current).setView(props.center, 15);
      
      // Add tile layer
      L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
        attribution: '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
      }).addTo(mapRef.current);
    }

    // Update markers when props change
    if (mapRef.current) {
      // Clear existing markers
      mapRef.current.eachLayer((layer) => {
        if (layer instanceof L.Marker) {
          layer.remove();
        }
      });

      // Add train stops
      props.trainStops?.forEach(stop => {
        L.marker([stop.latitude, stop.longitude], { icon: trainIcon })
          .bindPopup(`
            <div>
              <h3>${stop.stop_name}</h3>
              <p>Routes: ${stop.routes.join(', ')}</p>
              <p>Distance: ${stop.distance.toFixed(2)} miles</p>
              <button 
                onclick="window.addToMonitoring(${JSON.stringify(stop)})"
                style="
                  padding: 8px 16px;
                  background-color: #1976d2;
                  color: white;
                  border: none;
                  border-radius: 4px;
                  cursor: pointer;
                "
              >
                Add to Monitoring
              </button>
            </div>
          `)
          .addTo(mapRef.current!);
      });

      // Add bus stops
      props.busStops?.forEach(stop => {
        L.marker([stop.latitude, stop.longitude], { icon: busIcon })
          .bindPopup(`
            <div>
              <h3>${stop.stop_name}</h3>
              <p>Routes: ${stop.routes.join(', ')}</p>
              <p>Distance: ${stop.distance.toFixed(2)} miles</p>
              <button 
                onclick="window.addToMonitoring(${JSON.stringify(stop)})"
                style="
                  padding: 8px 16px;
                  background-color: #1976d2;
                  color: white;
                  border: none;
                  border-radius: 4px;
                  cursor: pointer;
                "
              >
                Add to Monitoring
              </button>
            </div>
          `)
          .addTo(mapRef.current!);
      });

      // Update center when it changes
      mapRef.current.setView(props.center);
    }

    // Add the monitoring function to the window object
    (window as any).addToMonitoring = (stop: Stop) => {
      props.onStopSelect(stop);
    };

    return () => {
      // Cleanup function to remove the window function
      delete (window as any).addToMonitoring;
    };
  }, [props.center, props.trainStops, props.busStops, props.onStopSelect]);

  return (
    <div 
      ref={containerRef}
      style={{ height: '500px', width: '100%', borderRadius: '8px' }}
    />
  );
};

export default StopsMap;
