import React, { useState, useEffect, useCallback } from 'react';
import {
  Box,
  Container,
  Typography,
  Paper,
  Grid,
  CircularProgress,
  Button,
  Tabs,
  Tab,
} from '@mui/material';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import StopsMap from './components/StopsMap';
import StopsList from './components/StopsList';
import ArrivalsList from './components/ArrivalsList';
import LocationSettings from './components/LocationSettings';

interface Stop {
  stop_id: number;
  stop_name: string;
  latitude: number;
  longitude: number;
  routes: string[];
  distance: number;
}

interface StopsResponse {
  train_stops: Stop[];
  bus_stops: Stop[];
}

const theme = createTheme({
  palette: {
    primary: {
      main: '#1976d2',
    },
    secondary: {
      main: '#dc004e',
    },
  },
});

const DEFAULT_LOCATION: [number, number] = [41.8781, -87.6298]; // Chicago

interface TabPanelProps {
  children?: React.ReactNode;
  index: number;
  value: number;
}

function TabPanel(props: TabPanelProps) {
  const { children, value, index, ...other } = props;

  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`simple-tabpanel-${index}`}
      aria-labelledby={`simple-tab-${index}`}
      {...other}
    >
      {value === index && (
        <Box sx={{ py: 3 }}>
          {children}
        </Box>
      )}
    </div>
  );
}

function App() {
  const [location, setLocation] = useState<[number, number]>(DEFAULT_LOCATION);
  const [loading, setLoading] = useState(false);
  const [stops, setStops] = useState<StopsResponse>({ train_stops: [], bus_stops: [] });
  const [monitoredStops, setMonitoredStops] = useState<Stop[]>([]);
  const [error, setError] = useState<string | null>(null);
  const [tabValue, setTabValue] = useState(monitoredStops.length > 0 ? 0 : 1);
  const [searchRadius, setSearchRadius] = useState(0.5);

  // Load monitored stops from backend on mount
  useEffect(() => {
    const loadMonitoredStops = async () => {
      try {
        const response = await fetch('http://localhost:8000/api/monitored-stops');
        if (response.ok) {
          const data = await response.json();
          setMonitoredStops(data);
          // If we have monitored stops, show the arrivals view by default
          if (data.length > 0) {
            setTabValue(0);
          }
        }
      } catch (err) {
        console.error('Error loading monitored stops:', err);
      }
    };
    loadMonitoredStops();
  }, []);

  const handleLocationUpdate = useCallback((lat: number, lon: number, radius: number) => {
    setLocation([lat, lon]);
    setSearchRadius(radius);
    fetchNearbyStops(lat, lon, radius);
  }, []);

  const getLocation = useCallback(() => {
    if (!navigator.geolocation) {
      setError('Geolocation is not supported by your browser');
      return;
    }

    setLoading(true);
    navigator.geolocation.getCurrentPosition(
      (position) => {
        const newLocation: [number, number] = [
          position.coords.latitude,
          position.coords.longitude
        ];
        setLocation(newLocation);
        fetchNearbyStops(newLocation[0], newLocation[1], searchRadius);
      },
      (error) => {
        setError('Error getting location: ' + error.message);
        setLoading(false);
      }
    );
  }, [searchRadius]);

  const fetchNearbyStops = async (lat: number, lon: number, radius: number) => {
    try {
      setLoading(true);
      const response = await fetch(`http://localhost:8000/api/stops?lat=${lat}&lon=${lon}&radius=${radius}`);
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const data = await response.json();
      setStops(data);
      setError(null);
    } catch (err) {
      setError('Error fetching stops: ' + (err as Error).message);
      setStops({ train_stops: [], bus_stops: [] });
    } finally {
      setLoading(false);
    }
  };

  const handleStopSelect = useCallback((stop: Stop) => {
    setMonitoredStops(prev => {
      if (!prev.some(s => s.stop_id === stop.stop_id)) {
        const newStops = [...prev, stop];
        // Save to backend
        fetch('http://localhost:8000/api/monitored-stops', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(newStops),
        }).catch(err => console.error('Error saving monitored stops:', err));
        return newStops;
      }
      return prev;
    });
  }, []);

  const handleStopRemove = useCallback((stopId: number) => {
    setMonitoredStops(prev => {
      const newStops = prev.filter(s => s.stop_id !== stopId);
      // Save to backend
      fetch('http://localhost:8000/api/monitored-stops', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(newStops),
      }).catch(err => console.error('Error saving monitored stops:', err));
      return newStops;
    });
  }, []);

  return (
    <ThemeProvider theme={theme}>
      <Container maxWidth="lg" sx={{ py: 4 }}>
        <Typography variant="h4" component="h1" gutterBottom>
          CTA Transit Tracker
        </Typography>

        {error && (
          <Paper sx={{ p: 2, mb: 2, bgcolor: '#ffebee' }}>
            <Typography color="error">{error}</Typography>
          </Paper>
        )}

        <Box sx={{ borderBottom: 1, borderColor: 'divider' }}>
          <Tabs value={tabValue} onChange={(_, newValue) => setTabValue(newValue)}>
            <Tab label="Arrival Times" disabled={monitoredStops.length === 0} />
            <Tab label="Find Stops" />
          </Tabs>
        </Box>

        <TabPanel value={tabValue} index={0}>
          {monitoredStops.length > 0 ? (
            <Grid container spacing={2}>
              {monitoredStops.map((stop) => (
                <Grid item xs={12} md={6} key={stop.stop_id}>
                  <Box sx={{ position: 'relative' }}>
                    <Button
                      size="small"
                      color="error"
                      onClick={() => handleStopRemove(stop.stop_id)}
                      sx={{ position: 'absolute', right: 8, top: 8, zIndex: 1 }}
                    >
                      Remove
                    </Button>
                    <ArrivalsList stop={stop} />
                  </Box>
                </Grid>
              ))}
            </Grid>
          ) : (
            <Typography variant="h6" sx={{ textAlign: 'center', mt: 4 }}>
              No stops monitored. Add some stops to see arrival times.
            </Typography>
          )}
        </TabPanel>

        <TabPanel value={tabValue} index={1}>
          <LocationSettings
            onLocationUpdate={handleLocationUpdate}
            loading={loading}
            onUseCurrentLocation={getLocation}
          />
          
          <Grid container spacing={2}>
            <Grid item xs={12} md={8}>
              <Paper sx={{ p: 2, mb: 2 }}>
                <StopsMap
                  trainStops={stops.train_stops}
                  busStops={stops.bus_stops}
                  center={location}
                  onStopSelect={handleStopSelect}
                />
              </Paper>
            </Grid>
            
            <Grid item xs={12} md={4}>
              <StopsList
                trainStops={stops.train_stops}
                busStops={stops.bus_stops}
                onStopSelect={handleStopSelect}
                monitoredStops={monitoredStops}
              />
            </Grid>
          </Grid>
        </TabPanel>
      </Container>
    </ThemeProvider>
  );
}

export default App;
