import React, { useState } from 'react';
import {
  Paper,
  Typography,
  TextField,
  Button,
  Box,
  Slider,
  Grid,
  CircularProgress,
} from '@mui/material';

interface LocationSettingsProps {
  onLocationUpdate: (lat: number, lon: number, radius: number) => void;
  loading: boolean;
  onUseCurrentLocation: () => void;
}

const LocationSettings: React.FC<LocationSettingsProps> = ({
  onLocationUpdate,
  loading,
  onUseCurrentLocation,
}) => {
  const [lat, setLat] = useState<string>('41.8781');
  const [lon, setLon] = useState<string>('-87.6298');
  const [radius, setRadius] = useState<number>(0.5);

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    const parsedLat = parseFloat(lat);
    const parsedLon = parseFloat(lon);
    if (!isNaN(parsedLat) && !isNaN(parsedLon)) {
      onLocationUpdate(parsedLat, parsedLon, radius);
    }
  };

  return (
    <Paper sx={{ p: 2, mb: 2 }}>
      <Typography variant="h6" gutterBottom>
        Location Settings
      </Typography>
      <form onSubmit={handleSubmit}>
        <Grid container spacing={2} alignItems="center">
          <Grid item xs={12} sm={6}>
            <TextField
              label="Latitude"
              value={lat}
              onChange={(e) => setLat(e.target.value)}
              fullWidth
              margin="normal"
              type="number"
              inputProps={{ step: 'any' }}
            />
          </Grid>
          <Grid item xs={12} sm={6}>
            <TextField
              label="Longitude"
              value={lon}
              onChange={(e) => setLon(e.target.value)}
              fullWidth
              margin="normal"
              type="number"
              inputProps={{ step: 'any' }}
            />
          </Grid>
          <Grid item xs={12}>
            <Typography gutterBottom>
              Search Radius: {radius} miles
            </Typography>
            <Slider
              value={radius}
              onChange={(_, value) => setRadius(value as number)}
              min={0.1}
              max={2}
              step={0.1}
              marks={[
                { value: 0.1, label: '0.1' },
                { value: 0.5, label: '0.5' },
                { value: 1, label: '1.0' },
                { value: 2, label: '2.0' },
              ]}
            />
          </Grid>
          <Grid item xs={12} sm={6}>
            <Button
              variant="contained"
              color="primary"
              type="submit"
              fullWidth
              disabled={loading}
            >
              {loading ? <CircularProgress size={24} /> : 'Search Area'}
            </Button>
          </Grid>
          <Grid item xs={12} sm={6}>
            <Button
              variant="outlined"
              color="primary"
              onClick={onUseCurrentLocation}
              fullWidth
              disabled={loading}
            >
              Use Current Location
            </Button>
          </Grid>
        </Grid>
      </form>
    </Paper>
  );
};

export default LocationSettings;
